import React from 'react'
import { Button, Header, Image, Modal } from 'semantic-ui-react'

function ModalImage() {
  const [open, setOpen] = React.useState(false)

  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Show Projects</Button>}
      style={{textAlign: 'center'}}
    >

      <Modal.Content image>
        <Image size='medium' src='/images/premium.png' wrapped width={250}/>
        <Modal.Description>

          <p>
          Basic Online Store for a Gadget Shop.
          </p>

        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        <Button color='black' onClick={() => setOpen(false)}>
          <a target="_blank" href='https://premium-gadgets.vercel.app/'>e-commerce</a>
        </Button>
        <Button
          content="Yep, that's me"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
}

export default ModalImage
