import React, { useRef } from "react";
import emailjs from '@emailjs/browser';


import "./contact.css";

const Contact = () => {

  const INITIAL_VALUES = { email: "", message: "" };
  const [notification, setNotification] = useState(false);
  const [values, setValues] = useState(INITIAL_VALUES);
  const [loading, setLoading] = useState(false);

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_f5fy1ef', 'template_w2e62xc', form.current, 'GJUsfDDktPfVReS-a')
      .then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      });

  };

  const clearForm = () => {
    setValues(INITIAL_VALUES);
  };

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  return (

    <section className="contact" name="contact" id="contact">
    <Heading text="Contact" style={{ marginBottom: "3rem" }} />
    <div className="content">
      <div className="left">
        <img src={shakeImage} alt="shake hands" />
      </div>
      <div className="right">
        <form onSubmit={handleSubmit} ref={form}>
          <input
            type="email"
            name="email"
            value={values.email}
            onChange={handleChange}
            placeholder="Email"
            required
          />
          <textarea
            name="message"
            required
            value={values.message}
            onChange={handleChange}
            placeholder="Message"
          ></textarea>
          <Button
            text={loading ? "Sending..." : "Send"}
            loading={loading}
            type="submit"
          />
          {notification && (
            <span style={{ color: "green", marginTop: "1rem" }}>
              Thanks, I will reply ASAP :)
            </span>
          )}
        </form>
      </div>
    </div>
  </section>
  );
};

export default Contact;
