import React from 'react'
import data from "../../Util/AppleProduct";
import { Link } from "react-router-dom";
import { Nav } from "react-bootstrap";

import CarouselStyled from './AppleCarousel';


import styled from "styled-components";



const Projects = () => {

  return (
    <>
      <Body>
        <CarouselStyled />
        <Details>
          <Name>
            <div className="brand">{data[0].brand}</div>
            <div className="name">{data[0].name}</div>
          </Name>
          <ProductDetails>
            <Description>{data[0].description}</Description>
          </ProductDetails>
          <Buttons>
            <div className="add-to-cart">
              < Nav.Link as={Link} to="/Apple/ApplePriceList">
                <button type="button">Order</button>
              </Nav.Link>
            </div>
          </Buttons>
        </Details>
      </Body>

    </>
  );
};

const Body = styled.div`
  max-width: 1152px;
  margin: auto;
  padding: 90px 50px 90px 50px;



  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(450px, 1fr));

  img {
    cursor: pointer;
    user-select: none;
  }

  @media (max-width: 1099px) {
    grid-column-gap: 40px;
  }

  @media (max-width: 1039px) {
    padding: 90px;
  }

  @media (max-width: 768px) {
    padding: 70px 0 0 0;
    grid-template-columns: 100%;
  }
`;




const Details = styled.div`
  padding: 40px;
  @media (max-width: 1099px) {
    padding: 40px 0;
  }
  @media (max-width: 768px) {
    padding: 20px;
  }
`;

const Name = styled.div`
  .brand {
    text-transform: uppercase;
    color: #324d67;
    font-weight: 700;
    font-size: 30px;
    letter-spacing: 1.5px;
    margin-bottom: 15px;
  }

  @media (max-width: 768px) {
    .name {
      font-size: 28px;
      margin-bottom: 20px;
    }
  }
`;

const ProductDetails = styled.div``;

const Description = styled.div`
  color: hsl(219, 9%, 45%);
  margin-bottom: 30px;
  line-height: 1.6;
  font-size: 18px;
`;


const Buttons = styled.div`
  display: flex;
  flex-wrap: wrap;
  color: #fff;


  .add-to-cart {
    button {
      height: 55px;
      width: 150px;
      background-color: #324d67;
      border: none;
      border-radius: 10px;
      color: #fff;
      align-items: center;
      display: flex;
      justify-content: center;

      img {
        margin-right: 10px;
      }

      color: white;
      font-weight: 700;
      cursor: pointer;
      transition: 100ms ease;
    }
  }

  @media (max-width: 499px) {
    .quantity {
      width: 100%;
      margin-bottom: 20px;
      margin-right: 0;
    }

    .add-to-cart {
      width: 100%;
      button {
        width: 100%;
      }
    }
  }
`;


export default Projects;
