import React from 'react';
import ParticlesBg from 'particles-bg';
import Roll from 'react-reveal/Roll';
import { TypeAnimation } from 'react-type-animation';
import { AiOutlineArrowDown } from "react-icons/ai"



const HomePage = () => {
    return (
        <React.Fragment>
            <div className='wrapper'>
                {/* <ParticlesBg color='#377D71' num={200} type='cobweb' bg={true} /> */}

                <div className='row banner'>
                    <div className='banner-text'>
                        <TypeAnimation
                            cursor={false}
                            sequence={[
                                'Sekitto Andrew',
                                2000,
                                'Full-Stack Developer',
                                2000,

                            ]}
                            wrapper="h2"
                            repeat={Infinity}
                            style={{ fontSize: '3em', color: 'rgb(34, 32, 32)', letterSpacing: '-2px', fontFamily: 'Aboreto' }}


                        />

                        <hr />
                        <Roll>
                            <h3>All i do is REACT & NODE with components to bring out the best feel of web development!</h3>
                        </Roll>
                    </div>


                    <p className="scrolldown">
                        <a className="smoothscroll" href="#about">
                            <AiOutlineArrowDown />
                        </a>
                    </p>
                </div>
            </div>
            <div className='row banner'>
                <div className='banner-text'>
                    <TypeAnimation
                        cursor={false}
                        sequence={[
                            'About Me',
                            2000,
                            'My Developer Profile',
                            2000,

                        ]}
                        wrapper="h2"
                        repeat={Infinity}
                        style={{ fontSize: '3em', color: 'rgb(34, 32, 32)', letterSpacing: '-2px', fontFamily: 'Aboreto' }}


                    />

                    <hr />

                    <h3>And this right here is my portfolio. Oh yeah,
                        ready to explore this ðŸ’Ž and get to grips with what the tech-world has to offer! By the way, check out my <a href="#project">skill-set</a>.
                        I'm a web developer with a full stack knowledge, but still exploring other technologies and frameworks to broaden my knowledge and understanding of the tech-world!
                    </h3>

                </div>

            </div>
        </React.Fragment>



    );

}




export default HomePage
